/**
 * processJSON (inputId, outputId)
 * 		inputId: id of object containing JSON
 * 		outputId: id of object to put html in
 */
function processJSON(inputId, outputId){
	var json = document.getElementById(inputId).innerHTML;
	var outputElement = document.getElementById(outputId);

	try {
		var obj = JSON.parse(json);
		var html = "";

		var treeRoot = 0;
		if (obj.constructor == Object) {
			html += getHtmlForObject(obj);
		} else if (obj.constructor == Array) {
			html += getHtmlForArray(obj);
		} else {
			html = "Invalid JSON";
		}
	} catch(err) {
		html = "<h1 class='error'>Invalid JSON</h1>";
		html += "<pre class='error'>" + err + "</pre>";
	}

	outputElement.innerHTML = html;
}

function basicEscape(html) {
	var _html = html;
	while (_html.indexOf('<') >= 0) {
		_html = html.replace(/</g, "&lt;").replace(/>/g, "&gt;").replace(/(?:\r\n|\r|\n)/g, '<br />');
	}
	_html = _html.replace(/(?:\r\n|\r|\n)/g, '<br />');
	return _html;
}

function getHtmlForArray(obj) {
	var html = "<ul class='array'>";
	for(i in obj) {
		var prefix = "<li>"; 
		var suffix = "</li>";
		if (obj[i] == null) {
			html += prefix + getHtmlForNull('null') + suffix ;			
		}
		if (obj[i].constructor == Object) {
			html += prefix + getHtmlForObject(obj[i]) + suffix;
		} else if (obj[i].constructor == Array) {
			html += prefix + getHtmlForArray(obj[i]) + suffix;
		} else if (obj[i].constructor == Number) {
			html += prefix + getHtmlForNumber(obj[i]) + suffix;
		} else if (obj[i].constructor == Boolean) {
			html += prefix + getHtmlForBoolean(obj[i]) + suffix;				
		} else {
			html += prefix + getHtmlForString(obj[i]) + suffix;
		}	
	}
	html += "</ul>";
	return html;
}

function getHtmlForObject(obj) {
	var html = "<ul class='object'>";
	for (var key in obj) {	
		var prefix = "<li>" + getHtmlForName(key);
		var suffix = "</li>";
		if (obj[key] == null) {
			html += prefix + getHtmlForNull('null') + suffix ;			
		} else if (obj[key].constructor == Object) {
			html += prefix + getHtmlForObject(obj[key]) + suffix;
		} else if (obj[key].constructor == Array) {
			html += prefix + getHtmlForArray(obj[key]) + suffix ;
		} else if (obj[key].constructor == Number) {
			html += prefix + getHtmlForNumber(obj[key]) + suffix;
		} else if (obj[key].constructor == Boolean) {
			html += prefix + getHtmlForBoolean(obj[key]) + suffix;				
		} else {
			html += prefix + getHtmlForString(obj[key]) + suffix;
		}	
	}
	html += "</ul>";
	return html;
}

function getHtmlForName(obj) {
	return "<span class='name' onclick='toggle(this.parentNode);'>" + basicEscape(obj) + "</span>";
}
function getHtmlForNumber(obj) {
	return "<span class='value number'>" + obj + "</span>";
}
function getHtmlForBoolean(obj){
	var string = 'true';
	if (! obj) {
		string = 'false';
	}
	return "<span class='value boolean'>" + string + "</span>";
}
function getHtmlForNull(obj) {
	return "<span class='value null'>" + obj + "</span>";
}
function getHtmlForString(obj) {
	return "<span class='value string'>" + basicEscape(obj) + "</span>";
}
function getTypeForObject(obj) {
	switch (obj.constructor) {
		case Array :
			return "Array";
			break;
		case Object:
			return "Object";
	}
}

/**
 * hide all but the first line
 * css must contain styles for hide class with max-height
 * the maxHeight var must have the same value as css property
 */
function toggle(e) {
	var maxHeight = 18;

	if (hasClass(e, 'hide')) {
		removeClass(e, 'hide');
	} else if (e.clientHeight > maxHeight) {
		// add hide class
		e.className = e.className + ' hide';
	}
}
function hasClass(element, cls) {
    return (' ' + element.className + ' ').indexOf(' ' + cls + ' ') > -1;
}
function removeClass(ele,cls) {
	var reg = new RegExp('(\\s|^)'+cls+'(\\s|$)');
	ele.className=ele.className.replace(reg,' ');
}

